    roots = f.small_roots(X=2^kbits, beta=0.4) #è¿›è¡Œçˆ†ç ´
    #print roots
    if roots:        #çˆ†ç ´æˆåŠŸï¼Œæ±‚æ ¹
        p = p4+int(roots[0])
        print(&amp;quot;p: &amp;quot;, hex(int(p)))
        assert n % p == 0
        q = n/int(p)
        print(&amp;quot;q: &amp;quot;, hex(int(q)))
        print(gcd(p,q))
        phin = (p-1)*(q-1)
        print(gcd(e2,phin))
        d = inverse_mod(e2,phin)
        print(&amp;quot;dï¼š&amp;quot;,d)
        flag = pow(cipher,d,n)
        flag = hex(int(flag))[2:]
        result = &amp;quot;&amp;quot;
