item.description:
 &lt;h1 id=&#34;hgame_wp_week1&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#hgame_wp_week1&#34;&gt;#&lt;/a&gt; Hgame_wp_week1&lt;/h1&gt;
&lt;p&gt;&lt;img data-src=&#34;/img/Hgame_wp_week1/image-20240204145551962.png&#34; alt=&#34;image-20240204145551962&#34;&gt;&lt;/p&gt;
&lt;p&gt;&lt;img data-src=&#34;/img/Hgame_wp_week1/image-20240205175025173.png&#34; alt=&#34;image-20240205175025173&#34;&gt;&lt;/p&gt;
&lt;h2 id=&#34;re&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#re&#34;&gt;#&lt;/a&gt; RE&lt;/h2&gt;
&lt;h3 id=&#34;ezasm100pt&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#ezasm100pt&#34;&gt;#&lt;/a&gt; ezASM(100pt)&lt;/h3&gt;
&lt;blockquote&gt;
&lt;p&gt;To learn a little ASM&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;题目：&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-assembly&#34;&gt;section .data
    c db 74, 69, 67, 79, 71, 89, 99, 113, 111, 125, 107, 81, 125, 107, 79, 82, 18, 80, 86, 22, 76, 86, 125, 22, 125, 112, 71, 84, 17, 80, 81, 17, 95, 34
    flag db 33 dup(0)
    format db &amp;quot;plz input your flag: &amp;quot;, 0
    success db &amp;quot;Congratulations!&amp;quot;, 0
    failure db &amp;quot;Sry, plz try again&amp;quot;, 0

section .text
    global _start

_start:
    ; Print prompt
    mov eax, 4
    mov ebx, 1
    mov ecx, format
    mov edx, 20
    int 0x80

    ; Read user input
    mov eax, 3
    mov ebx, 0
    mov ecx, flag
    mov edx, 33
    int 0x80

    ; Check flag
    xor esi, esi
check_flag:
    mov al, byte [flag + esi]
    xor al, 0x22
    cmp al, byte [c + esi]
    jne failure_check

    inc esi
    cmp esi, 33
    jne check_flag

    ; Print success message
    mov eax, 4
    mov ebx, 1
    mov ecx, success
    mov edx, 14
    int 0x80

    ; Exit
    mov eax, 1
    xor ebx, ebx
    int 0x80

failure_check:
    ; Print failure message
    mov eax, 4
    mov ebx, 1
    mov ecx, failure
    mov edx, 18
    int 0x80

    ; Exit
    mov eax, 1
    xor ebx, ebx
    int 0x80

&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;解题思路：&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;; Check flag&lt;br&gt;
xor esi, esi&lt;br&gt;
check_flag:&lt;br&gt;
mov al, byte [flag + esi]&lt;br&gt;
xor al, 0x22&lt;br&gt;
cmp al, byte [c + esi]&lt;br&gt;
jne failure_check&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;检测用户输入与 0x22 异或然后与 c 进行比较&lt;/p&gt;
&lt;p&gt;即 c [i] 与 0x22 异或之后得到的为 flag 内容。&lt;/p&gt;
&lt;p&gt;exp:&lt;/p&gt;
&lt;figure class=&#34;highlight python&#34;&gt;&lt;figcaption data-lang=&#34;python&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;c &lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;[&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;74&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;69&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;67&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;79&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;71&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;89&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;99&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;113&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;111&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;125&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;107&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;81&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;125&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&gt;107&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt; &lt;span class=&#34;token number&#34;&g