    for x in range(width):
        r, g, b = image.getpixel((x, y))
        if r == 46:
            print(&#39;.&#39;,end=&amp;quot;&amp;quot;)
        elif r == 33:
            print(&#39;!&#39;,end=&amp;quot;&amp;quot;)
        elif r == 63:
            print(&#39;?&#39;,end=&amp;quot;&amp;quot;)
        if g == 46:
                print(&#39;.&#39;,end=&amp;quot;&amp;quot;)
        elif g == 33:
                print(&#39;!&#39;,end=&amp;quot;&amp;quot;)
        elif g == 63:
                print(&#39;?&#39;,end=&amp;quot;&amp;quot;)
        if b == 46:
            print(&#39;.&#39;,end=&amp;quot;&amp;quot;)
        elif b == 33:
            print(&#39;!&#39;,end=&amp;quot;&amp;quot;)
        elif b == 63:
            print(&#39;?&#39;,end=&amp;quot;&amp;quot;)
