&lt;h3 id=&#34;ç­¾åˆ°é¢˜æ¥å’¯&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#ç­¾åˆ°é¢˜æ¥å’¯&#34;&gt;#&lt;/a&gt; ç­¾åˆ°é¢˜æ¥å’¯ï¼&lt;/h3&gt;
&lt;pre&gt;&lt;code class=&#34;language-Python&#34;&gt;import libnum
import binascii
from  Crypto.Util.number import *
n = 
c1 = 
c2 = 
def is_prime(n):
    if n &amp;lt;= 1:
        return False
    if n &amp;lt;= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i &amp;lt;= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True    
def franklinReiter(n,e,c1,c2):
    PR.&amp;lt;x&amp;gt; = PolynomialRing(Zmod(n))
    g1 = (114*x+2333)^int(e) - c1
    g2 = (514*x+4555)^int(e) - c2
    def gcd(g1, g2):
        while g2:
            g1, g2 = g2, g1 % g2
        return g1.monic()
    return -gcd(g1, g2)[0]

for i in range(1024):
    if is_prime(i):
        e = i
        m=franklinReiter(n,e,c1,c2)
        print(long_to_bytes((int(m))))
