r.sendlineafter(&amp;quot;name: &amp;quot;, &amp;quot;abf1ag&amp;quot;)
r.sendlineafter(&amp;quot;age: &amp;quot;, &amp;quot;18&amp;quot;)
r.sendlineafter(&amp;quot;address: &amp;quot;, &amp;quot;aaaa&amp;quot;)
